public class ShiftOperatorsExample {
    public static void main(String[] args) {
        int num = 8; // Binary: 00001000

        // Left shift (<<): Multiplies by 2
        int leftShift = num << 2; // 8 * 2^2 = 32 (Binary: 00100000)
        System.out.println("Left Shift: " + leftShift);

        // Signed right shift (>>): Divides by 2
        int rightShift = num >> 2; // 8 / 2^2 = 2 (Binary: 00000010)
        System.out.println("Right Shift: " + rightShift);

        // Unsigned right shift (>>>): Works the same for positive numbers
        int unsignedRightShift = num >>> 2; // 8 / 2^2 = 2 (Binary: 00000010)
        System.out.println("Unsigned Right Shift: " + unsignedRightShift);
    }
}

public class DisplayDigits {
    public static void main(String[] args) {
        if (args.length != 1) {
            System.out.println("Usage: java DisplayDigits <number>");
            return;
        }

        String number = args[0];

        for (char digit : number.toCharArray()) {
            if (!Character.isDigit(digit)) {
                System.out.println("Invalid input! Please enter a valid number.");
                return;
            }
        }

        System.out.println("Displaying digits with a 10-second interval:");

        for (char digit : number.toCharArray()) {
            System.out.println(digit);
            try {
                Thread.sleep(10000); // 10-second delay
            } catch (InterruptedException e) {
                System.out.println("Thread interrupted");
            }
        }
    }
}

