        print('Even Number');
    } else {
        print('Odd Number');
    }

    // if-else if Ladder
    if (number > 0) {
        print('Positive');
    } else if (number < 0) {
        print('Negative');
    } else {
        print('Zero');
    }

    // switch Statement
