import java.util.*;

class Employee implements Comparable<Employee> {
    String name; // Employee's name
    int age; // Employee's age
    double salary; // Employee's salary

    Employee(String name, int age, double salary) { // Constructor
        this.name = name;
        this.age = age;
        this.salary = salary;
    }

    @Override
    public int compareTo(Employee other) { // For sorting by salary
        return Double.compare(this.salary, other.salary);
    }

    @Override
    public String toString() { // For displaying employee details
        return "Name: " + name + ", Age: " + age + ", Salary: " + salary;
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in); // Scanner for user input
        List<Employee> employees = new ArrayList<>(); // List to store employees

