import java.applet.Applet;
import java.awt.*;

public class CircleTriangle extends Applet {
    public void paint(Graphics g) {
        int centerX = 150, centerY = 150, radius = 100; // Center and radius of circle

        // Draw and fill circle in blue
        g.setColor(Color.BLUE);
        g.fillOval(centerX - radius, centerY - radius, 2 * radius, 2 * radius);

        // Calculate triangle vertices to touch circle perimeter
        int[] xPoints = {centerX, centerX + (int)(radius * Math.cos(Math.PI / 6)), 
                         centerX - (int)(radius * Math.cos(Math.PI / 6))};
        int[] yPoints = {centerY - radius, centerY + (int)(radius * Math.sin(Math.PI / 6)), 
                         centerY + (int)(radius * Math.sin(Math.PI / 6))};
        // Draw and fill triangle in yellow
        g.setColor(Color.YELLOW);
        g.fillPolygon(xPoints, yPoints, 3);

        // Output: A graphical window (300x300 pixels) displaying a blue filled circle
        // with a yellow filled equilateral triangle inside it, where all three vertices
        // of the triangle touch the circle’s perimeter.
    }
}

