class Node {
    int data; // Data for the node
    Node next; // Reference to next node

    Node(int data) { // Constructor
        this.data = data;
        this.next = null;
    }
}

class LinkedList {
    Node head; // Head of the list

    // Insert node at specific position
    void insertAtPos(int data, int pos) {
        Node newNode = new Node(data);
        if (pos == 1) { // Insert at start
            newNode.next = head;
            head = newNode;
            return;
        }
        Node temp = head;
        for (int i = 1; i < pos - 1 && temp != null; i++) { // Traverse to position-1
            temp = temp.next;
        }
        if (temp != null) { // Insert after temp
            newNode.next = temp.next;
            temp.next = newNode;
        }
    }

    // Delete node at specific position
    void deleteAtPos(int pos) {
        if (head == null) return; // Empty list
        if (pos == 1) { // Delete head
            head = head.next;
            return;
        }
        Node temp = head;
        for (int i = 1; i < pos - 1 && temp != null; i++) { // Traverse to position-1
            temp = temp.next;
        }
        if (temp != null && temp.next != null) { // Delete node after temp
            temp.next = temp.next.next;
        }
    }

    // Display the linked list
    void display() {
        Node temp = head;
        while (temp != null) {
            System.out.print(temp.data + " -> ");
            temp = temp.next;
        }
        System.out.println("null");
    }
}

public class Main {
    public static void main(String[] args) {
        LinkedList list = new LinkedList(); // Create list
        list.insertAtPos(10, 1); // Insert 10 at pos 1
        list.insertAtPos(20, 2); // Insert 20 at pos 2
        list.insertAtPos(15, 2); // Insert 15 at pos 2
        System.out.println("Original List:");
        list.display();

        list.deleteAtPos(2); // Delete node at pos 2
        System.out.println("After deleting at position 2:");
        list.display();

        // Output:
        // Original List:
        // 10 -> 15 -> 20 -> null
        // After deleting at position 2:
        // 10 -> 20 -> null
    }
}

