import java.applet.Applet;
import java.awt.*;

public class DrawShape extends Applet {
    public void paint(Graphics g) {
        int centerX = 150, centerY = 150, radius = 100; // Center and radius

        // Draw blue circle
        g.setColor(Color.BLUE);
        g.fillOval(centerX - radius, centerY - radius, 2 * radius, 2 * radius);

        // Calculate triangle vertices
        int[] xPoints = {centerX, centerX + (int)(radius * Math.cos(Math.PI / 6)), 
                         centerX - (int)(radius * Math.cos(Math.PI / 6))};
        int[] yPoints = {centerY - radius, centerY + (int)(radius * Math.sin(Math.PI / 6)), 
                         centerY + (int)(radius * Math.sin(Math.PI / 6))};
        // Draw yellow triangle
        g.setColor(Color.YELLOW);
        g.fillPolygon(xPoints, yPoints, 3);

        // Write 'Java' in center
        g.setColor(Color.BLACK);
        g.drawString("Java", centerX - 15, centerY);

        // Output: A graphical window (300x300 pixels) showing a blue filled circle,
        // a yellow filled triangle inside it with vertices touching the circle,
        // and 'Java' written in black at the center.
    }
}

