import java.util.Scanner;

class MyString {
    String str; // String data member

    MyString(String s) { // Constructor
        str = s;
    }

    String reverse() { // Reverse the string
        return new StringBuilder(str).reverse().toString();
    }

    String toggleCase() { // Toggle case of each character
        char[] chars = str.toCharArray();
        for (int i = 0; i < chars.length; i++) {
            if (Character.isUpperCase(chars[i])) chars[i] = Character.toLowerCase(chars[i]);
            else if (Character.isLowerCase(chars[i])) chars[i] = Character.toUpperCase(chars[i]);
        }
        return new String(chars);
    }

    String sentenceCase() { // Capitalize first letter, rest lowercase
        if (str.length() == 0) return str;
        return Character.toUpperCase(str.charAt(0)) + str.substring(1).toLowerCase();
    }

    String extractNFromRight(int n) throws IllegalArgumentException { // Extract N chars from right
        if (n > str.length()) throw new IllegalArgumentException("N exceeds string length");
        return str.substring(str.length() - n);
    }
}

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        System.out.print("Enter a string: ");
        MyString ms = new MyString(sc.nextLine()); // Create MyString object

        while (true) {
            // Display menu
            System.out.println("\n1. Reverse\n2. Toggle Case\n3. Sentence Case\n4. Extract N from Right\n5. Exit");
            System.out.print("Choice: ");
            int choice = sc.nextInt();

            try {
                switch (choice) {
                    case 1: System.out.println("Reverse: " + ms.reverse()); break;
                    case 2: System.out.println("Toggle: " + ms.toggleCase()); break;
                    case 3: System.out.println("Sentence: " + ms.sentenceCase()); break;
                    case 4: 
                        System.out.print("Enter N: ");
                        int n = sc.nextInt();
                        System.out.println("Extracted: " + ms.extractNFromRight(n)); 
                        break;
                    case 5: System.exit(0); // Exit program
                    default: System.out.println("Invalid choice");
                }
            } catch (IllegalArgumentException e) {
                System.out.println("Error: " + e.getMessage());
            }
        }

        // Sample Output for input "Hello World":
        // Enter a string: Hello World
        // 
        // 1. Reverse
        // 2. Toggle Case
        // 3. Sentence Case
        // 4. Extract N from Right
        // 5. Exit
        // Choice: 1
        // Reverse: dlroW olleH
        // 
        // 1. Reverse
        // 2. Toggle Case
        // 3. Sentence Case
        // 4. Extract N from Right
        // 5. Exit
        // Choice: 2
        // Toggle: hELLO wORLD
        // 
        // 1. Reverse
        // 2. Toggle Case
        // 3. Sentence Case
        // 4. Extract N from Right
        // 5. Exit
        // Choice: 3
        // Sentence: Hello world
        // 
        // 1. Reverse
        // 2. Toggle Case
        // 3. Sentence Case
        // 4. Extract N from Right
        // 5. Exit
        // Choice: 4
        // Enter N: 5
        // Extracted: World
        // (Continues until 5 is chosen)
    }
}

import java.applet.Applet;
import java.awt.*;

public class ShapeApplet extends Applet {
    String shape; // Shape name from parameter

    public void init() { // Initialize applet
        shape = getParameter("shape"); // Get shape from HTML param
    }

    public void paint(Graphics g) {
        int centerX = 150, centerY = 150, size = 100; // Center and size

        g.setColor(Color.RED); // Set fill color to red
        if (shape.equalsIgnoreCase("rectangle")) { // Draw rectangle
            g.fillRect(centerX - size, centerY - size, 2 * size, 2 * size);
        } else if (shape.equalsIgnoreCase("oval")) { // Draw oval
            g.fillOval(centerX - size, centerY - size, 2 * size, 2 * size);
        } else if (shape.equalsIgnoreCase("triangle")) { // Draw triangle
            int[] xPoints = {centerX, centerX + size, centerX - size};
            int[] yPoints = {centerY - size, centerY + size, centerY + size};
            g.fillPolygon(xPoints, yPoints, 3);
        }

        g.setColor(Color.BLACK); // Set text color to black
        g.drawString("VNSGU", centerX - 20, centerY); // Write VNSGU in center

        // Output: A graphical window (300x300 pixels) showing a red filled oval
        // (if 'oval' is passed in param), with 'VNSGU' written in black at the center.
        // Shape changes to rectangle or triangle based on the 'shape' parameter value.
    }
}

